vec3 palette(float t)
{
    vec3 a = vec3(0.938, 0.328, 0.718);
    vec3 b = vec3(0.659, 0.438, 0.328);
    vec3 c = vec3(0.388, 0.388, 0.296);
    vec3 d = vec3(2.538, 2.478, 0.168);

    return a + b*cos( 6.28318*(c*t+d) );
}

float sdRandom(vec3 p){
    float s = sin(p.x + iTime*0.4);
    float c = cos(p.y + iTime*0.4);
    float t = tan(p.x + iTime*0.4);
    return length(vec3(s, c, t)* 0.02);
}


float map(vec3 p) {
    return sdRandom(p); 
}

mat2 rot2D(float angle){
    float s = sin(angle);
    float c = cos(angle);
    return mat2(c, -s, s, c);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord * 2. - iResolution.xy) / iResolution.y;


    vec3 ro = vec3(0, 0, -3);         
    vec3 rd = normalize(vec3(uv, 1)); 
    vec3 col = vec3(0);               

    rd.xy *= rot2D(sin(3.14/2.));
    ro.xy *= rot2D(sin(3.14/2.));


    float t = 0.; 


    for (int i = 0; i < 80; i++) {
        vec3 p = ro + rd * t;     

        float d = map(p);         

        t += d;                   

        if (d < .001) break;      
        if (t > 100.) break;      
    }


    col = palette(t * .2);           

    fragColor = vec4(col, 1);
}
