precision mediump float;

void mainImage( out vec4 fragColor, in vec2 fragCoord  ) {\
	vec2 origin = fragCoord.xy / iResolution.xy;
	origin.x *= iResolution.x / iResolution.y;

	vec2 position =  vec2(iResolution / iResolution / vec3(2));
	position.x *= iResolution.x / iResolution.y;
	float fill = sin(length(origin - position) * 7.5 * 0.4) * 1.5  + 0.7;

	float a = 1.0;
	float sc = 37.3;
	vec2 p = origin * sc * mat2(cos(a), -sin(a), sin(a), cos(a));
	p.x += iTime;
	p = mod(p, 1.0) - 0.5;
	float r = length(p) * 15.0 - 1.0 + ( 6.0 * fill );

	a = 3.0;
	sc = 43.7;
	p = origin * sc * mat2(cos(a), -sin(a), sin(a), cos(a));
	p.x += iTime;
	p = mod(p, 1.0) - 0.5;
	float g = length(p) * 15.0 - 10.0 + ( 6.0 * fill );

	a = 5.0;
	sc = 46.3;
	p = origin * sc * mat2(cos(a), -sin(a), sin(a), cos(a));
	p.x += iTime;
	p = mod(p, 1.0) - 0.5;
	float b = length(p) * 15.0 - 10.0 + ( 6.0 * fill );

	fragColor = vec4(r, g, b, 1.0);
}
