float edge(float orient, float start, float end, float blur){
    float mask1 = smoothstep(start-blur, start+blur, orient);
    float mask2 = smoothstep(end+blur, end-blur, orient);

    return mask1*mask2;
}

float rectangle(vec2 uv, float x1, float x2, float y1, float y2, float blur){
    float mask1 = edge(uv.x, x1, x2, blur);
    float mask2 = edge(uv.y, y1, y2, blur);

    return mask1*mask2;
}

