void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 uv_org = uv;   
    vec3 fcol = vec3(.0);
    float t = iTime * 0.2;
    float st = sin(t);
    vec3 a = vec3(.3, .1,  .2) + st;

    for (int i = 0; i < 5; i++) {
        uv = fract(2. + uv * sin(t)) - 0.5;

        float b = mod((iTime * 0.7), 42.);
        vec3 c = sin(float(i) * t * a);

        vec3 d  = vec3(length(uv) * (float(i) * 1.5) * b * c);
        d = cos(d) / 3.;
        d = abs(d);
        d = smoothstep(0.0, .7, d);

        vec3 col = sin(0.1 + sin(t) + float(i) + uv_org.xyx * uv.xyx);
        fcol += col * d / length(uv_org);
    }
    fragColor = vec4(fcol,1.0);
}
