vec2 polarToCartesian(float radius, float angle)
{
    return vec2(radius*cos(angle), radius*sin(angle));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized coordinates
	vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
    vec2 m = (2.0*iMouse.xy-iResolution.xy)/iResolution.y;

    // Polar coordinates
    float radius = length(p);
    float angle = atan(p.y,p.x)-iTime;

    float swirl = 30. + 5.*(1.-m.x);
    vec2 pSwirled = polarToCartesian(0.1*radius, angle + pow(radius, 0.2)*swirl) + vec2(-0.01*iTime);

    float noiseSample = texture(iChannel0, pSwirled).x;
    vec3 noiseCol = 1.2*texture(iChannel1, pSwirled).xyz;
    float emission = clamp(3.*(noiseSample - 0.5), 0., 1.);

    // Output to screen
    fragColor = vec4(noiseCol*emission, 1.0);
}
