void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 a = vec3(sin(iTime * vec3(0.5, 0.2, 0.3)));
    vec3 b = vec3(-500, 200, 500);
    vec3 c = vec3(0.2, 1.4, 0.4);
    vec3 col = 0.2 + sin(0.5 + c * iTime) + sin(atan(uv.yyy, uv.xxx) * b * a);


    fragColor = vec4(col,1.0);
}
