#define SHARPEN_STRENGTH 30.0

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord / iResolution.xy;
    vec2 offset = vec2(1, 1) / iResolution.xy;

    vec4 left   = texture(iChannel0, uv + vec2(-offset.x, 0));
    vec4 right  = texture(iChannel0, uv + vec2(offset.x,  0));
    vec4 up     = texture(iChannel0, uv + vec2(0,         offset.y));
    vec4 down   = texture(iChannel0, uv + vec2(0,         -offset.y));
    vec4 avg = (up + left + right + down)*0.25;

    vec4 center = texture(iChannel0, uv);

    // Compute sharpen classic (top-left)
    vec4 result = center + SHARPEN_STRENGTH*(center - avg);

    // Compute sharpen in sqrt space (bottom-right)
    vec4 sqAvg = sqrt(avg);
    vec4 sqCenter = sqrt(center);
    vec4 sqResult = sqCenter + sqrt(SHARPEN_STRENGTH)*(sqCenter - sqAvg);


    float halfScreen = step(uv.x-uv.y, 0.0);
    fragColor = mix(result, sqResult*sqResult, halfScreen);
}
