float rand(vec2);
vec2 offset = vec2(0., 0.);
vec2 cell = vec2(0., 0.);
float cellsize = 1.;

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    float scale = cellsize; //cellsize + (abs(sin(iTime * 0.5)) * 10.);
    cell = floor(fragCoord / scale) * scale;
    highp float red = rand(cell.xy+vec2(0.5, 0.5));
    highp float green = rand(cell.xy);
    highp float blue = rand(cell.xy+vec2(-.5, -.5))* abs(sin(cell.x));
    fragColor = vec4(red, green, blue, 1.0);
}

