// You can use this shader as a template for ray marching shaders

#define MAX_STEPS 44
#define MAX_DIST 50.
#define SURF_DIST .01
#define TAU 6.283185
#define PI 3.141592
#define S smoothstep
#define T iTime

float rand(vec2 n) { 
	return fract(sin(dot(n, vec2(12.9898, 4.1414))) * 43758.5453);
}

float noise(vec2 p){
	vec2 ip = floor(p);
	vec2 u = fract(p);
	u = u*u*(3.0-2.0*u);

	float res = mix(
		mix(rand(ip),rand(ip+vec2(1.0,0.0)),u.x),
		mix(rand(ip+vec2(0.0,1.0)),rand(ip+vec2(1.0,1.0)),u.x),u.y);
	return res*res;
}

mat2 Rot(float a) {
    float s=sin(a), c=cos(a);
    return mat2(c, -s, s, c);
}

float sdBox(vec3 p, vec3 s) {
    p = abs(p)-s;
	return length(max(p, 0.))+min(max(p.x, max(p.y, p.z)), 0.);
}
float sdSphere(vec3 p, float s) {
    return length(p) - s;
}

float opSmoothUnion( float d1, float d2, float k )
{
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h);
}


float sdGyroid(vec3 p, float scale, float thickness, float bias) {
    p*=scale;
    return abs(dot(sin(p), cos(p.zxy))- bias)/scale-thickness;
}


float GetDist(vec3 p) {
    //p.y -= T*0.002;

    float box = sdSphere(p, 1.75+1.*sin(T));
    float sphere = sdSphere(p, 3.0 - 0.*(cos(T)*0.5+.5));
    p.z += .2*noise(p.xy);
    p.xy *= Rot(.2*noise(p.zy));
    p.yx *= Rot(iTime/9.);

   	float g1 = sdGyroid(p, 0.63 + sin(T)*.5*noise(p.xy+T), .09-0.1*abs(sin(T+PI/2.)), 1.)/2.;
    float g2 = sdGyroid(p, 10.76, .03, .3);
    float g3 = sdGyroid(p, 20.76, .03, .3);
    float g4 = sdGyroid(p, 35.76, .03, .3);
    float g5 = sdGyroid(p, 60.76, .03, .3);
    float g6 = sdGyroid(p, 80.76, .003, .3);

    g1 -= g2*.4;
    //g1 -= g3*.3;
    //g1 -= g4*.2;
    //g1 -= g5*.2;
    g1 += g6* (0.1+(.3*abs(sin(T/3.))));

    vec3 q = p;
    q.x = fract(q.x)-.5;
    q.y = fract(q.y)-.5;

    float psphere = sdSphere(q,4.);

    float r = min(box,g1*0.6);
    //return g1;
    return max(g1, psphere);
    return max(sphere, opSmoothUnion(r, box, 1.));
}

float RayMarch(vec3 ro, vec3 rd) {
	float dO=0.;

    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = GetDist(p);
        dO += dS;
        if(dO>MAX_DIST || abs(dS)<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
    vec2 e = vec2(.001, 0);
    vec3 n = GetDist(p) - 
        vec3(GetDist(p-e.xyy), GetDist(p-e.yxy),GetDist(p-e.yyx));

    return normalize(n);
}

vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 
        f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = f*z,
        i = c + uv.x*r + uv.y*u;
    return normalize(i);
}


vec3 palette (float t) {
    vec3 a = vec3(sin(iTime), 0.5, cos(iTime*5.));
    vec3 b = vec3(0.5, sin(iTime/2.)*2., 0.5);
    vec3 c = vec3(1., 1., 1.);
    vec3 d = vec3(0.263, 0.416, 0.557);
    return a + b*cos( 6.283185*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
	vec2 m = iMouse.xy/iResolution.xy;

    vec3 ro = vec3(0, 9., -0.0);
    ro.yz *= Rot(-m.y*3.);
    ro.xz *= Rot(-m.x*3.);

    vec3 rd = GetRayDir(uv, ro, vec3(0,0,0), 0.7);
    vec3 col = vec3(0);

    float d = RayMarch(ro, rd);
    vec3 n;
    vec3 p;
    if(d<MAX_DIST) {
        p = ro + rd * d;
        n = GetNormal(p);
        vec3 r = reflect(rd, n);

        float dif = n.y*.5+.5;
        col = vec3(dif);
        col *= vec3(2.,0.3,0.1);
        col.r -= n.x;
        col.g *= 2.*n.z;
        col *= dot(col,n);

        //col.r *= n.r * cos(T);
        //col.g *= n.g * sin(T);
        //col.b *= n.b;
    }

    col *= dot(col,palette(d*0.05));

    col = pow(col, vec3(.4545));	// gamma correction

    if (d>20.) {
        col.r = uv.y * sin(uv.x*5.);
        col.g = uv.y*.3;
        col.b = -uv.y*.3;
    }

    fragColor = vec4(col,0.);
}
