// Green screen function from @iq 

// :D :D :D
vec4 compositeGreenScreenTexture(vec2 uv, vec4 background)
{

     vec4 tex = texture(iChannel0, uv);

     float maxrb = max( tex.r, tex.b );
     float k = clamp( (tex.g-maxrb)*5.0, 0.0, 1.0 );

     float ll = length( tex );
     tex.g = min( tex.g, maxrb*0.8 );
     tex = ll*normalize(tex);

     return mix(tex, background, k);

}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    //produce a gradient that changes over time.
    float r = sin((uv.x + uv.y) * 30.0);
    float g = sin((uv.x + uv.y) * 30.0 + iTime);
    float b = sin((uv.x + uv.y) * 30.0 - iTime);

    vec4 col = vec4(r, g, b, 1.0);

    col = compositeGreenScreenTexture(fragCoord/iResolution.xy, col);

    fragColor = col;
}
