//inspiration from haquxx: https://www.shadertoy.com/view/3tdSRn
const float DIRECTION = 1.;
const float SPEED = 1.5;
const float radius = .5;
const float width = .8;
const float power = .5;

float mask(vec2 pos)
{
    float dist1 = length(pos);
    return dist1;
}

vec3 drawCircle(vec2 pos, float radius, float width, float power)
{
    vec2 mousePos = iMouse.xy - vec2(0.5);
    float dist1 = length(pos);
    dist1 = fract((dist1 * 5.0) - fract(iTime*SPEED)* DIRECTION);
    float dist2 = dist1 - radius;
    float intensity = pow(radius / abs(dist2), width); 
    vec3 col = vec3(intensity * power * max((0.8- abs(dist2)), 0.0));
    return col;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pos = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);

    vec3 finalColor = drawCircle(pos, radius, width, power);

    fragColor = vec4(clamp(finalColor, 0.0, 1.0)*(mask(pos*vec2(0.5)) * (1.0-mask(pos*vec2(0.9)))*6.  ), 1.0);
}
