#define rot(a) mat2(cos(a + vec4(0, 11, 33, 0)))
#define t iTime
#define fogo vec3(10, 4, 0)
#define nuvem vec3(1, 1, 2) / 2.2

void mainImage(out vec4 o, vec2 u) {    
    vec2 r = iResolution.xy;

    float i, dd, dd1, dd2, d = 1., a;
    vec3 p, q, D, cor;

    // camera
    p = vec3(0, 0, cos(t * .5) * 3.);
    p.zx *= rot(sin(t * .01) * 10.);
    p.zy *= rot(cos(t * .01) * 10.);

    D = vec3((u - r.xy / 2.) / r.y, 1.4);
    D.zy *= rot(cos(t * 3.) * .05);
    D.zx *= rot(sin(t * .1) * .5);

    while (i++ < 50. && d > .01) {
        cor = nuvem;
        a = 1.; d = -.4;
        while(a < 128.)
            q = p,
            q.xy *= rot(a),
            q.yz *= rot(a),
            q = fract(q * a) - .5,
            d += length(q) * .7 / a,
            a += a;

        // fogo
        q = p - sin(
                    vec3(3.1, 1.7, 1.4) * t + vec3(0, 11, 0))
                        * vec3(.5, .5, 1) - vec3(0, 0, 2);
        q.xy *= rot(t * 3.);
        q.zy *= rot(t * 2.);
        dd1 = dot(abs(q), vec3(.4)) - .1;

        q.yx *= rot(t * 5.);
        q.zx *= rot(t * 4.);
        dd2 = dot(abs(q), vec3(.4)) - .1;

        dd = min(dd1, dd2);

        1.4 * dd < d
            ? cor = fogo, d = .01 * dd
            : d;

        p += D * d * .4; 
    }

    o.rgb = 1. - exp(-1.5 * cor / i + i / 50. - .5);
}
