void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    float t = iTime * 0.2; 

    float overallRotation = t * 1.3; 
    mat2 overallRot = mat2(cos(overallRotation), -sin(overallRotation), sin(overallRotation), cos(overallRotation));
    uv = overallRot * uv;

    float zoom = 1.0 + sin(t * 0.2) * 0.1;
    uv *= zoom;

    float z = 1.21 / (length(uv) + 0.1);
    uv *= z;

    float angle = t * 0.5;
    mat2 rot = mat2(cos(angle), -sin(angle), sin(angle), cos(angle));
    uv = rot * uv;

    float phi = atan(uv.y, uv.x);
    float theta = acos(length(uv));
    vec3 sphericalUV = vec3(sin(theta) * cos(phi), sin(theta) * sin(phi), cos(theta));

    vec3 O = vec3(0.0);
    for (float i = 0.0; i < 6.0; i += 1.0)
    {
        float l = length(vec2(cos(i + t), sin(i - t)) + sphericalUV.xy);
        O += pow(
            0.09 / abs(sin(exp(sin(l) * 1.0 - length(sphericalUV.xy * 0.4)) * 8.0 + t * 4.0) 
            - smoothstep(0.0, 0.4, l - 0.4) * 1.5) / 
            smoothstep(0.0, 0.09, abs(l - 0.1)), 1.0
        ) * (1.0 + cos(i * 0.55 + l * 2.0 - t * 4.0 + vec3(0.0, 1.0, 2.0)));

    }

    float centerMask = smoothstep(0.0, 0.01, length(uv));
    O *= centerMask;

    O *= smoothstep(1.0, 0.2, length(uv) * 0.7);

    fragColor = vec4(O, 1.0);
}
