// Fork of "chechered pattern" by jedikoops. https://shadertoy.com/view/X3cSDn
// 2024-07-01 16:14:05

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 xy = fragCoord/iResolution.xy;

    //if scale = 2 then it will be a 2x2 checker pattern.
    float scale = 20.0*sin(iTime)+102.0;


    //colours the screen white
    fragColor = vec4(1.0,1.0,1.0,1.0);

    //scale represents the magnitude of one unit
    //in one unit there is two black squares, top right and bottom left
    //and there are two white squares, top left and bottom right

    //the first nested if statements finds the top right square and colours it black
    //the second nested if statements finds the bottom left square and colours black

    //mod(fragCoord.x, scale) gets the remainder after dividing the pixel by scale
    //so that it can be repeated multiple times

    if(mod(fragCoord.x, scale) >= scale/2.0){
        if(mod(fragCoord.y, scale) >= scale/2.0){
            fragColor = vec4(0,0,0,0);
        }
    }

    if(mod(fragCoord.x, scale) < scale/2.0){
        if(mod(fragCoord.y, scale) < scale/2.0){
            fragColor = vec4(0,0,0,0);
        }
    }

}
