vec3 palette(float t){
    vec3 a = vec3(0.5,0.5,1.0);
    vec3 b = vec3(0.5,0.5,1.0);
    vec3 c = vec3(0.5,0.5,1.);
    vec3 d = vec3(0.2,0.4,0.6);

    return a + b*cos(6.28318*(c*t + d));

}

float drawCircle(float r,vec2 pos,vec2 uv){
    float circle = length(uv - pos);
    circle = smoothstep(r,r+0.01,circle);
    return circle;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv0 =fragCoord/iResolution.xy;
    vec2 uv =(2.0*fragCoord - iResolution.xy)/iResolution.y;

    vec3 col = palette(length(uv) + iTime );

    float circle = length(uv)-0.8;
    float smoothCircle = smoothstep(0.01,0.,circle);
    float outedge =clamp ( 0.03/clamp(circle,0.,1.),0.,1.) - smoothCircle;

    float circle1 = drawCircle(0.4,vec2(0.,.405),uv) * step(uv.x,0.);
    float circle2 = drawCircle(0.4,vec2(0.,-.405),uv);
          circle2 = (1.-circle2) * step(0.,uv.x);
    float circle3 = drawCircle(0.2,vec2(0.,.405),uv);
          circle3 = 1.-circle3;
    float circle4 = drawCircle(0.2,vec2(0.,-.405),uv);
          circle4 = 1.-circle4;

    vec4 taiji=  vec4(circle1 + circle2 + circle3 - circle4) ;

    fragColor  =taiji * smoothCircle + outedge * vec4(col,1.);      
}
