float rotCircle(float r,vec2 pos,vec2 uv){

    float speed = -iTime*5.;

    pos.x = -pos.y*sin(speed);
    pos.y = cos(speed)*pos.y;

    float circle = length(uv - pos);
    circle = smoothstep(r,r+0.01,circle);
    return circle;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{       
    vec2 uv0 =fragCoord/iResolution.xy;
    vec2 uv =(2.0*fragCoord - iResolution.xy)/iResolution.y;

    float circle = length(uv)-0.8;
    float smoothCircle = smoothstep(0.01,0.,circle);
    float outedge =clamp ( 0.03/clamp(circle,0.,1.),0.,1.) - smoothCircle;

    float speed = iTime*5.;
    float rotuvx = uv.x*cos(speed) -uv.y*sin(speed);
    float edgeuv = step(0.,rotuvx);

    float circle1 = rotCircle(0.4,vec2(0.,.405),uv) *edgeuv ;
          circle1 = clamp(circle1,0.,1.);
    float circle2 = rotCircle(0.4,vec2(0.,-.405),uv);
          circle2 = (1.-circle2) *(1.-edgeuv);
          circle2 = clamp(circle2,0.,1.);
    float circle3 = rotCircle(0.2,vec2(0.,.405),uv);
          circle3 = 1.-circle3;
    float circle4 = rotCircle(0.2,vec2(0.,-.405),uv);
          circle4 = 1.-circle4;

    vec4 taiji=  vec4(circle1 + circle2 +  circle3  - circle4) ; 

    fragColor  =taiji * smoothCircle + outedge;   
}
