#ifdef GL_FRAGMENT_PRECISION_HIGH
precision highp float;
#else
precision mediump float;
#endif

mat2 Rot(float angle)
{
  return(mat2(
    cos(angle),
    -sin(angle),
    sin(angle),
    cos(angle)
    ));
}

float random (vec2 st)
{
    return fract(sin(dot(st.xy,
    vec2(12.9898,78.233)))*
    43758.5453123);
}

float stars(vec2 coord)
{
  float starValue = 0.0;

  float amount = 10.0;
  float minF = -2.0;
  float maxF = 2.0;

  for (float i = 1.0; i < amount; i+=1.0)
  {
    float timeValue = iTime * 0.5 * random(vec2(i+amount,i-amount));
    float timeFrac = fract(timeValue);
    float timeFloor = floor(timeValue);
    float next = timeFloor + 1.0;

    vec2 currentCoord = vec2(mix(minF, maxF, random(vec2(timeFloor*i))), mix(minF, maxF, random(vec2(timeFloor+i))));
    vec2 nextCoord = vec2(mix(minF, maxF, random(vec2(next*i))), mix(minF, maxF, random(vec2(next+i))));
    vec2 mixCoord = mix(currentCoord, nextCoord, timeFrac);
    starValue += 0.005/distance(mixCoord, coord);
  }

  return starValue;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = fragCoord.xy / iResolution.xy;
  float ratio = iResolution.x/iResolution.y;
  uv *= 2.0;
  uv -= 1.0;
  uv.y /= ratio;;

  float colorAmount = 0.01/length(uv);
  colorAmount += stars(uv);

  uv*=(Rot(iTime*2.0));

  vec4 color = vec4(1,1,1,1);

  color.r*=max(1.0-fract(iTime), fract(iTime));
  color.g*=max(1.0-sin(iTime), sin(iTime));
  color.b*=max(1.0-cos(iTime), cos(iTime));

  colorAmount += 0.1 * (1.0 - (step(0.006, abs(uv.x)*abs(uv.y))))/length(uv);
  uv*=(Rot(iTime*1.5));
  colorAmount += 0.05 * (1.0 - (step(0.006, abs(uv.x)*abs(uv.y))))/length(uv);
  uv*=(Rot(iTime));
  colorAmount += 0.025 * (1.0 - (step(0.006, abs(uv.x)*abs(uv.y))))/length(uv);

  fragColor = color*colorAmount;
}
