// TV Static Shader

#define TIME iTime

void mainImage( out vec4 fragColor, in vec2 fragCoord ) 
{
    // Randomize seed based on pixel position
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 seed = uv * TIME;

    // Generate random value
    float random = fract(sin(dot(seed.xy, vec2(12.9898, 78.233))) * 43758.5453);

    // Quantize into levels to create banding    
   random = floor(random * 8.0) / 8.0;

   // Apply static effect 
   fragColor = vec4(random, random, random, 1.0);
}
