void mainImage(out vec4 O, in vec2 I)
{
    vec2 uv = 3. * (2. * I - iResolution.xy) / iResolution.y;
    float dist = length(uv);
    float a = 0.25 * iTime;
    for(float i; i < 6.; i++)
    {
        float scale = exp2(-i);
        dist = abs(dist - scale) - 0.5 * scale;
        uv *= mat2(cos(a), -sin(a), sin(a), cos(a));
        dist += 0.25 * fract(i * 0.375) * abs(abs(uv.x) - abs(uv.y));
    }
    float val = 0.04 / max(dist, 0.);
    vec3 col = val * vec3(0.35, 0.5, 1.);
    O = vec4(col,1.0);
}
