void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 p = fragCoord/iResolution.xy;
    vec2 q = p - vec2(0.5,0.5);

    vec3 col = vec3(1.0, 0.4, 0.1 );


    // (0,0), (1, 1) = 0.707
    // (0.5, 0), (0.5, 1) = 0.5 
    //d=  (p.x−0.5)  +(p.y−0.5)  so it fallsi n(0, 0.707)
    // step < 0.5, 0, else 1  
    //col *= step(0.5, length(q)) ;

    // <0.25 : 0, >0.5, 1; 否则就是中间0.25 -  0.5
    //黑 - 黑0渐变到红1【在这个区间】 - 红
    //col *= smoothstep(0.2, 0.3, length(q));

    float r = 0.2 + 0.1 * cos(atan(q.y, q.x) * 2.0 + q.x * 5.0);
    col *= smoothstep( r, r + 0.01, length(q));

    float center = 0.005;
    vec3 centerColor = vec3(0.0, 0.0, 1.0);
    col = mix(centerColor, col, step(center, length(q)));

    fragColor = vec4(col,1.0);
}
