#define PI 3.141592

void mainImage(out vec4 c_out, in vec2 u)
{


    vec2 d = iResolution.xy;
    vec2 uv = (u + u - d) / d.y;
    float g = sin(length(uv) * 3.0 + iTime);
    vec2 uvc = vec2(length(uv), atan(uv.y + sin(uv.x), uv.x));
    uvc.y = abs(uv.y);
    uvc *= vec2(cos(iTime * 0.25), sin(iTime));
    uv *= 1.2;
    uvc *= 0.7;


    float w = tan(uvc.x * 2.0 + uvc.y * 3.0);
    w = exp(w * 0.1);
    vec3 clr = vec3(0.0);

    for (float i = 0.0; i < 11.0; i++) {

        uvc = vec2(length(uvc), atan(uvc.y, uvc.x));
        uv = fract(uv * (1.0 / g) + iTime) - 0.5;

        float fr = sin(uv.x + iTime + i * 2.5);
        float fg = cos(uvc.y + iTime + i * 3.0);
        float fb = tan(uvc.x + uv.y + iTime + i * 7.0);

        vec2 ofsr = vec2(cos(iTime + i), sin(iTime + i)) * 0.25 + vec2(fr);
        vec2 ofsg = vec2(cos(iTime + PI + 3.0 * i), sin(iTime + PI)) * 0.25 + vec2(fg);
        vec2 ofsb = vec2(cos(iTime + PI * 1.5 + 1.5 * i), sin(iTime + PI * 1.5)) * 0.25 + vec2(fb);

        float r = smoothstep(0.0, length(uv + ofsr), 0.2);
        float g = smoothstep(0.0, length(uv + ofsg), 0.1);
        float b = smoothstep(0.0, length(uvc + ofsb), 0.14);

        clr += vec3(r, g, b) * w;


    }

    clr *= 0.2;
    clr = pow(clr, vec3(3.0));
    c_out = vec4(clr, 1.0);


}
