#define FREQ 12.0
const vec3 BG_COLOR = vec3(0.1,0.6,0.8);
const vec3 FG_COLOR = vec3(0.4,0.88,1.0);
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //-1 to 1, aspect ratio corrected
    vec2 uv = ( fragCoord - 0.5 * iResolution.xy ) / iResolution.y;
    uv*=FREQ;
    vec2 cell = floor(uv);
    uv = fract(uv);
    //Circle center of each cell
    vec2 center = vec2(0.5);
    //Diagonal scroll
    float timeOffset = cell.x + cell.y;
    //Rotate each circle center within cell
    center+=vec2(cos(iTime+timeOffset),sin(iTime+timeOffset))*0.15;
    float d = distance(uv,center);
    //Pulse radius
    float r = 0.2+sin(iTime+timeOffset)*0.1;
    d = smoothstep(r-0.01,r+0.01,d); 
    d = 1.0 - d;
    //Fade circle intensity
    float i = smoothstep(0.0,0.3,r);
    d*=i;
    vec3 col = mix(BG_COLOR,FG_COLOR,d);
    fragColor = vec4(col,1.0);
}
