void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Define screen coordinates
    vec2 uv = fragCoord.xy / iResolution.xy;

    float intensity = sin(iTime);

    // Define the position of the sine wave along the x-axis
    float sinePosition = 0.5 + 0.125 * sin(uv.x * 10.0 - iTime * 4.0)* cos(uv.x * 50.0  - iTime * 3.0);

    float sinePosition2 = 0.5 + 0.125 * abs(sin(uv.x * 10.0 - iTime * 4.0));

    // Determine the color based on the position relative to the sine wave
    vec3 colortop = vec3(step(uv.y, sinePosition));

    vec3 colorbot= vec3(step(sinePosition2, uv.y));

    vec3 colorbotx= vec3(step(sinePosition, uv.y + 0.05)) * vec3(1,0,0);

    vec3 color = colortop + colorbot + colorbotx;

    // Output final color
    fragColor = vec4(color, 1.0);
}
