void mainImage( out vec4 O, vec2 U ){

    vec2 r = iResolution.xy;
    vec2 uv = U/r;
    vec2 cuv = (2.*U-r)/r.y;
    vec2 muv = (2.*iMouse.xy-r)/r.y;

    float radius = cos(iTime) * .4 + .5;

    O = vec4(clamp(
        (dot(cuv, cuv) - radius*radius)/(2.*radius)*r.y/2.
    ,0., 1.));
}
