// -2 thanks to fabrice ðŸª¶

void mainImage( out vec4 o, vec2 u )
{
    o.xyz = iResolution;
    u = .7 * (u+u-o.xy)/o.y;

    for (float i, t = .2 * iTime; 
         ++i < 5.;
         o -= i / abs( dot(u,u) + .5*tanh(8.*cos(t+t)) - .5 )
         )     
        u = vec2(dot(u,u), sin(i*atan(u.y, u.x)))
          * mat2(cos(t - vec4(0,11,33,0)));

    o = exp(o.zzzz / 2e2);
}


