void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;
    vec2 texelSize = 1. / iResolution.xy;

    vec2 blockCoord = floor(fragCoord / BLOCK_SIZE) * BLOCK_SIZE;
    vec2 blockUV = blockCoord / iResolution.xy;

    vec2 bestMatch = vec2(0.0);
    float bestMatchError = 1.0 / 0.0; // Large number

    for (float dy = -SEARCH_RADIUS; dy <= SEARCH_RADIUS; dy++) {
        for (float dx = -SEARCH_RADIUS; dx <= SEARCH_RADIUS; dx++) {
            vec2 searchBlockCoord = blockCoord + vec2(dx, dy) * BLOCK_SIZE;
            vec2 searchUV = searchBlockCoord / iResolution.xy;

            if (searchUV.x < 0. || searchUV.x > 1. || searchUV.y < 0. || searchUV.y > 1.)
                continue;

            float error = 0.0;
            for (float by = 0.0; by < BLOCK_SIZE; by++) {
                for (float bx = 0.0; bx < BLOCK_SIZE; bx++) {
                    vec2 offset = vec2(bx, by) * texelSize;
                    vec4 currentColor = texture(iChannel1, blockUV + offset);
                    vec4 previousColor = texture(iChannel0, searchUV + offset);
                    error += distance(currentColor, previousColor);
                }
            }

            if (error < bestMatchError) {
                bestMatchError = error;
                bestMatch = vec2(dx, dy);
            }
        }
    }

    vec2 flow = bestMatch * BLOCK_SIZE / iResolution.xy;
    vec3 flowColor = vec3(flow * 1. + 0.5, 0.0);
    bool mousePressed = bool(iMouse.z > 0.5);
    if (mousePressed) {
        fragColor = texture(iChannel2, uv);
        return;
    }
    fragColor = vec4(flowColor, 1.0);
}

vec4 blurHorizontal(sampler2D tex, vec2 uv, vec2 resolution, float radius) {
    vec4 color = vec4(0.0);
    float totalWeight = 0.0;
    vec2 texelSize = vec2(radius) / resolution;

    for (float x = -radius; x <= radius; x += 2.0) {
        vec2 offset = vec2(x, 0.0) * texelSize;
        vec4 sampleColor = texture(tex, uv + offset);
        color += sampleColor;
        totalWeight += 1.0;
    }

    return color / totalWeight;
}

vec4 blurVertical(sampler2D tex, vec2 uv, vec2 resolution, float radius) {
    vec4 color = vec4(0.0);
    float totalWeight = 0.0;
    vec2 texelSize = vec2(radius) / resolution;

    for (float y = -radius; y <= radius; y += 2.0) {
        vec2 offset = vec2(0.0, y) * texelSize;
        vec4 sampleColor = texture(tex, uv + offset);
        color += sampleColor;
        totalWeight += 1.0;
    }

    return color / totalWeight;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord.xy / iResolution.xy;

    if (texelFetch(iChannel3, ivec2(32, 0), 0).x != 0.0) {
        fragColor = texture(iChannel0, uv);
        return;
    }

    vec4 feedbackColor = texture(iChannel2, uv);
    vec2 opticalFlow = texture(iChannel1, uv).rg;
    vec4 blurredOpticalFlowHorizontal = blurHorizontal(iChannel1, uv, iResolution.xy, 5.0);
    vec4 blurredOpticalFlow = blurVertical(iChannel1, uv, iResolution.xy, 5.0);
    vec2 displacedUV = uv + (blurredOpticalFlow.xy - vec2(.5)) * DISPLACEMENT;
    vec4 displacedFeedback = texture(iChannel2, displacedUV);
    vec4 finalColor = mix(displacedFeedback, texture(iChannel0, uv), PERSISTENCE);
    fragColor = finalColor;
}
