#define u 25.0
void mainImage( out vec4 O, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 i = 1./iResolution.xy;
    vec2 uv = fragCoord*i;
    vec3 bl = vec3(0.0);
    float bls=0.;
    float tt = clamp(0.,1.,texture(iChannel0,uv).r);
    if (texture(iChannel0,uv).r>=0.)
    {
        for (float n=-u*tt;n<u*tt;n++)
        {
            for (float m=-u*tt;m<u*tt;m++)
            {
                float t = texture(iChannel0,uv+vec2(n,m)*i).r;
                if (t>0.)
                {
                    bl+=texture(iChannel1,uv+vec2(n,m)*i).rgb;
                    bls++;
                }
            }
        }
        O = vec4(bl/bls-.1,1);
    }
    else O = texture(iChannel1,uv);
    // Output to screen
    //O = texture(iChannel0,uv);
}

void mainImage( out vec4 O, in vec2 F )
{
    vec2 i = 1./iResolution.xy;
    F*=i;
    float d = length(iMouse.xy-F/i);
    if (iMouse.z>.1 && d<60.) O=vec4(60.-d)/60.+texture(iChannel0,F);
    else
    {
        O = smoothstep(0.,1.,texture(iChannel0,F)-.0001)-.1;
        //O
    };
    //O.r=clamp(0.,1.,O.r);
}
