void mainImage(out vec4 O,vec2 F   )
{
    O.r = step(texture(iChannel0,F*.125).r,F.x/iResolution.x);
    vec3 a = vec3(0,1,0);
    vec3 b = vec3(1,0,0);
    //O.r=sqrt(O.r);
    O.rgb=a*O.r-b*O.r+b;
}
