#define MAX_ITERATIONS 1000

#define MAX_DISTANCE 10000.
#define MIN_DISTANCE .001

#define SUN_DIR normalize(vec3(3.,7.,4.))

#define METALLIC 1.
#define SMOOTHNESS 0.

#define MENGER_SPONGE_ROUNDNESS .005

float scene(vec3 ro) {
    vec3 o = vec3(-1.);

    float scale = 1.;
    for (int i = 0; i < 15; i++) {
        ro *= 3.;
        scale *= 3.;
        ro -= 1.;

        ro = abs(ro-o)+o;
        fold(o, vec3(0., 1., -1.), ro);
        fold(o, vec3(-1., 1., 0.), ro);
        fold(o+vec3(0.,.5,0.), vec3(0.,-1.,0.), ro);
    }

    return sdfSphere(ro, 0.5)/scale-MENGER_SPONGE_ROUNDNESS;
}

vec3 sdfNormal(vec3 ro) {
    const float h = 0.0001; // replace by an appropriate value
    const vec2 k = vec2(1,-1);
    return normalize( k.xyy*scene( ro + k.xyy*h ) + 
                      k.yyx*scene( ro + k.yyx*h ) + 
                      k.yxy*scene( ro + k.yxy*h ) + 
                      k.xxx*scene( ro + k.xxx*h ) );
}

vec3 render(vec3 ro, vec3 rd) {
    vec3 col = texture(iChannel0, rd).rgb;

    float t = 0.;
    for (int i = 0; i < MAX_ITERATIONS; i++) {
        vec3 p = ro + rd*t;
        float dist = scene(p);

        if (dist <= MIN_DISTANCE) {
            vec3 normal = sdfNormal(p);
            float light = max(dot(normal, SUN_DIR),0.1);
            vec3 diffuse = vec3(1.,.4,.7);

            vec3 r = reflect(rd, normal);
            vec3 specularCrisp = texture(iChannel0,r).rgb;
            vec3 specularBlurred = texture(iChannel1,r).rgb;
            vec3 specular = mix(specularBlurred, specularCrisp, SMOOTHNESS);

            float fresnelFactor = max(pow(1.-dot(normal,r),5.),0.03);
            vec3 fresnel = specular*fresnelFactor;

            col = mix(diffuse*light + fresnel*SMOOTHNESS, diffuse*specular, METALLIC);

            break;
        }
        if (dist >= MAX_DISTANCE) {
            break;
        }

        t += dist;
    }

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-iResolution.xy/2.)/iResolution.y;
    vec2 m = (iMouse.xy-iResolution.xy/2.)/iResolution.y;

    vec3 ro = vec3(0,0,2);
    ro = rot3D(ro,-m.y*4.,-m.x*4.);
    vec3 rd = getRayDir(ro,vec3(0),uv,45.);

    vec3 col = render(ro, rd);

    fragColor = vec4(col,1.0);
}

vec3 getRayDir(vec3 ro, vec3 wo, vec2 uv, float FOV) {
    float t = tan(FOV*3.14159/180.);
    vec3 f = normalize(wo-ro),
        r = normalize(cross(vec3(0,1,0),f)),
        u = cross(f,r),
        rd = f+r*uv.x*t+u*uv.y*t;
    return normalize(rd);
}
mat2 rot2D(float angle) {
    float s = sin(angle);
    float c = cos(angle);
    return mat2(
        c,-s,
        s,c
    );
}
vec3 rot3D(vec3 p, float pitch, float yaw) {
    p.zy *= rot2D(pitch);
    p.xz *= rot2D(yaw);
    return p;
}
void fold(vec3 o, vec3 n, inout vec3 ro) {
    n = normalize(n);
    float d = dot(ro-o, n);
    ro -= 2.*n*max(d,0.);
}

//SDF's
float sdfSphere(vec3 ro, float r) {
    return length(ro)-r;
}
