float Circle(vec2 uv, vec2 p, float r, float blur) {
    float d = length(uv - p);

    return smoothstep(r, r - blur, d);
}

float Smiley(vec2 uv, vec2 p, float size) {
    uv -= p;
    uv *= size;

    float mask = Circle(uv, vec2(0), 0.8, 0.05);

    mask -= Circle(uv, vec2(-0.4, 0.2), .2, .01);
    mask -= Circle(uv, vec2(0.4, 0.2), .2, .01);


    float mouth = Circle(uv, vec2(.0, -.2), 0.3, 0.01);
    mouth -= Circle(uv, vec2(.0, -.1), 0.3, 0.01);
    return mask - mouth;
}

float Band(float t, float start, float end, float blur) {
 float step1 = smoothstep(start-blur, start + blur, t);
 float step2 = smoothstep(end+blur, end -blur, t);
 return step1 * step2;
}

float Rect(vec2 uv, float left, float right, float bottom, float top, float blur) {
    float band1 = Band(uv.x, left, right, blur);
    float band2 = Band(uv.y, bottom, top, blur);
    return band2 * band1;
}
float remap01(float a, float b, float t) {
    return (t-a) /(b-a);
}


float remap(float a, float b, float c, float d, float t) {
    return mix(c,d, remap01(a,b,t));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy * 2. - 1.;
    float aspect = iResolution.x/iResolution.y;
    uv.x *= aspect;


    vec3 col = vec3(1., .0, 1.);


    // float mask = Smiley(uv, vec2(0., .1), 1.);

    float mask = 0.;

    float x = uv.x;
    float m = sin(x*8.+iTime)*.1;
    float y = uv.y+m;

    float blur = remap(-1., 1., 0.01, 0.1, x);

    mask = Rect(vec2(x,y), -1., 1., -.1, .1, blur);
    col *= mask;

    // Output to screen
    fragColor = vec4(col,1.0);
}
