void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalizamos las coordenadas
    vec2 uv = fragCoord / iResolution.xy;

    // Dividimos la pantalla en dos mitades
    float halfWidth = 0.5;
    bool isLeft = uv.x < halfWidth; // Verdadero si está en la mitad izquierda

    // Generamos el strobo para la mitad izquierda (verde) y derecha (amarillo)
    float strobe1 = mod(iTime * 5.0, 2.0);  // Strobo para el verde (más lento)
    float strobe2 = mod(iTime * 5.0 + 1.0, 2.0);  // Strobo para el amarillo (desplazado)

    // Convierte el strobo en 0 o 1
    strobe1 = step(1.0, strobe1);
    strobe2 = step(1.0, strobe2);

    // Colores base
    vec3 color = vec3(0.0, 0.0, 0.0); // Fondo negro

    // Strobo verde en la mitad izquierda
    if (isLeft && strobe1 > 0.5) {
        color = vec3(0.0, 1.0, 0.0); // Verde
    }

    // Strobo amarillo en la mitad derecha
    if (!isLeft && strobe2 > 0.5) {
        color = vec3(1.0, 1.0, 0.0); // Amarillo
    }

    // Asignamos el color final al fragmento
    fragColor = vec4(color, 1.0);
}
