// main stuff
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    int mode = 0;







    //univursall varibels
    vec2 uv = (fragCoord * 2.-iResolution.xy) / iResolution.y;
    vec2 uv_0 = uv;
    vec3 finalColor = vec3(0.0);


    switch(mode)
{


//mode 0 default
case 0:
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    fragColor = vec4(col,1.0);

}break;

//mode 1 stubidly beutiful
case 1:
{

    for(float i = 0.0; i < 4.0; i++){
        uv = fract(uv * 1.5)- 0.5;

        float d = length(uv) * exp(-length(uv_0));

        vec3 col = palette(length(uv_0) + i*.4 + iTime * 0.4);

        d = sin(d * 8.0 + iTime)/8.0;
        d = abs(d);

        d =  pow(0.01/d,1.8);

        finalColor += col * d;    
    } 
    fragColor = vec4(finalColor,1.0);

}break;


//mode 2 heart experiament
case 2:
{


    vec3 col = palette_1(length(uv_0) - iTime * 0.4);
    float d = sdHeart(uv*1.5);
    d = 0.1+sin(d * 8.0 - iTime)/8.0;
    d = abs(d);
    d = pow(0.01/d,1.8);
    finalColor += col * d;    

fragColor = vec4(finalColor,1.0);
}break;
}
}

//outside stuff
float dot2( in vec2 v ) { return dot(v,v); }
float sdHeart( in vec2 p )
{
    p.x = abs(p.x);
    if( p.y+p.x>1. )
        return sqrt(dot2(p-vec2(0.25,0.75))) - sqrt(2.0)/4.0;
    return sqrt(min(dot2(p-vec2(0.00,1.00)),
                    dot2(p-0.5*max(p.x+p.y,0.0)))) * sign(p.x-p.y);
}

vec3 palette(float t){
    vec3 a = vec3(0.5,0.5,0.5);
    vec3 b = vec3(0.5,0.5,0.5);
    vec3 c = vec3(1.0,1.0,1.0);
    vec3 d = vec3(0.263,0.416,0.557);
    return a + b * cos(6.28318*(c*t+d));
}

vec3 palette_1(float t){
    vec3 a = vec3(0.938, 0.328, 0.718);
    vec3 b = vec3(0.659, 0.438 ,0.328);
    vec3 c = vec3(0.388, 0.388, 0.296);
    vec3 d = vec3(2.538, 2.478 ,0.168);
    return a + b * cos(6.28318*(c*t+d));
}
