vec2 ToTexUv(in vec2 uv)
{
	return vec2(0.156 + 0.1559 * uv.x, uv.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Get one cat
    vec2 uv = ToTexUv(fragCoord / iResolution.xy);

    vec4 texColor = texture(iChannel0, uv);

    // Calculate the gradient of the alpha channel
    float dx = dFdx(texColor.a);
    float dy = dFdy(texColor.a);
    float gradient = length(vec2(dx, dy));

    if (gradient > 0.01)
        fragColor = vec4(1.,.7,.9,1.);//mix(texColor, vec4(1.,.7,.9,1.), 10. * gradient);
    else
        fragColor = texColor;
}
