// Shadertoy sphere inversion shader

#define MAX_STEPS 10
#define MAX_DIST 50.
#define SURF_DIST .001

// Time-based animation
float getTime() {
    return iTime * 0.5;
}

// Hexagonal grid
vec2 hexGrid(vec2 p) {
    const float hexSize = 0.1;
    vec2 r = vec2(1, 1.7320508); // sqrt(3) pre-calculated
    vec2 h = r * 0.5;
    vec2 a = mod(p, r) - h;
    vec2 b = mod(p - h, r) - h;
    return dot(a, a) < dot(b, b) ? a : b;
}

// Inversion in a circle
vec3 invertPoint(vec3 p, float radius) {
    float r2 = radius * radius;
    return p * r2 / dot(p, p);
}

// Distance function
float getDist(vec3 p) {
    float time = getTime();

    // Apply hexagonal grid deformation
    vec2 hexOffset = hexGrid(p.xy * 5.0) * 0.8;
    p.xy += hexOffset;

    // Inversion
    float invRadius = 1.0 + sin(time) * 0.5;
    p = invertPoint(p, invRadius);

    // Create spheres
    float sphereDist = length(p) - 0.5;
    float sphereDist2 = length(p) - 1.5;
    float sphereDist3 = length(p) - 0.5;
    // Add some variation
    sphereDist2 += sin(p.x * 10.0 + time) * 0.1;
    sphereDist2 += sin(p.y * 5.0 + time * 0.3) * 0.1;
    sphereDist += sin(p.z * 22.0 + time * 0.07) * 0.1;
    sphereDist += sin(p.z * 22.0 + time * 0.07) * 0.1;
    sphereDist += sin(p.x * 10.0 + time) * 0.01;
    sphereDist3 += sin(p.y * 5.0 + time * 0.3) * 0.1;
    sphereDist3 += sin(p.z * 22.0 + time * 0.07) * 0.1;
    return sphereDist;
}

// Ray marching
float rayMarch(vec3 ro, vec3 rd) {
    float dO = 0.;
    for(int i = 0; i < MAX_STEPS; i++) {
        vec3 p = ro + rd * dO;
        float dS = getDist(p);
        dO += dS;
        if(dO > MAX_DIST || abs(dS) < SURF_DIST) break;
    }
    return dO;
}

// Calculate normal
vec3 getNormal(vec3 p) {
    float d = getDist(p);
    vec2 e = vec2(.001, 0);
    vec3 n = d - vec3(
        getDist(p - e.xyy),
        getDist(p - e.yxy),
        getDist(p - e.yyx));
    return normalize(n);
}

// Main function
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    vec3 ro = vec3(0, 1, -3);
    vec3 rd = normalize(vec3(uv.x, uv.y, 1));

    float d = rayMarch(ro, rd);
    vec3 p = ro + rd * d;

    vec3 n = getNormal(p);
    vec3 r = reflect(rd, n);

    // Lighting
    vec3 light = normalize(vec3(1, 2, -0.3));
    float diff = dot(n, light) * 0.5 + 0.5;
    float spec = pow(max(dot(r, light), 0.0), 30.0);
    float fresnel = pow(1.0 - max(dot(n, -rd), 0.0), 5.0);

    // Color
    vec3 col = vec3(2.2, 0.1, 0.4);
    col = mix(col, vec3(4.8, 0.7, 0.5), diff);
    col += vec3(5.0, 5.8, 6.7) * spec * 0.5;
    col += vec3(0.6, 0.2, 7.3) * fresnel * 0.5;

    // Output
    fragColor = vec4(col, 10.0);
}
