void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy/iResolution.xy;

    // Image
    vec4 texture01 = texture(iChannel0, uv);
    vec3 col = texture(iChannel0, uv).rgb;
    vec3 col0 = col;

    float oldx = col.x;
    float oldy = col.y;
    float add = abs(oldx - oldy) * 0.5;
    float stepxy = step(col.y, col.x);
    float stepyx = 1.0 - stepxy;
    col.x = stepxy * (oldx + add) + stepyx * (oldx - add);
    col.y = stepyx * (oldy + add) + stepxy * (oldy - add);

    // Brightness 
    col.z = sqrt(col.z * 2.5);

    col = mix(col0, col, 1.0);

    vec4 colorsPhoto = vec4(texture01.r + sin(-1.5 * iTime + uv.x * 2.5) * 5.5, texture01.g , texture01.b, 1.0);

    vec3 mixedColor = mix(col, colorsPhoto.rgb, 0.55);

    vec3 finalColor = mix(mixedColor, texture01.rgb, 0.75);

    //fragColor = texture01;
    //fragColor = vec4(mixedColor, 1.0);
    fragColor = vec4(finalColor, 1.0);
}
