#define TAO 6.2831853
#define S smoothstep
vec2 R(vec2 u, float a) { return mat2(cos(a), sin(a), -sin(a), cos(a)) * u; }
float N(vec2 uv, float t, float p) {
    vec2 a = vec2(0.), res = vec2(0.);
    float s = 10.;
    for (int j = 0; j < 30; j++) {
        uv = R(uv, 1.);
        a = R(a, 1.);
        vec2 L = uv * s + float(j) + a - t;
        a += cos(L);
        res += (.5 + .5 * sin(L)) / s;
        s *= (1.2 - .07 * p);
    }
    return res.x + res.y;
}
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 U = fragCoord / iResolution.y;
    float T = mod(iTime * TAO, TAO);
    float H = clamp(.5 * sin(T) * sin(T / 2.) * exp(-T / 4.) + .5, 0., 1.);
    float n = N(U, H * 5., .1) * 1.15;
    vec3 C = mix(mix(vec3(0.), vec3(1., 0., .2), S(1., 1., n)), mix(vec3(1., 0., .2), vec3(1., .635, 0.), S(.5, 1., n)), S(0., 1., n));
    fragColor = vec4(C, 1.0);
}
