float lineBox(vec2 p){
    float w = 0.01;
    float l = 1.;
    p = abs(p);
    vec2 d = smoothstep(w, w*.5, abs(vec2(l)- p));
    return d.x + d.y;
}


float orts(vec2 p){
    vec2 c = step(abs(p), vec2(0.003));
    return c.x + c.y;
}

float qubic(vec2 p){
    p *= 10.;
    float A = 5.0, B = 0., C = 9., D=-30./2., E =18./2., F=9.;
    float d = A*p.x*p.x + 2.*B*p.x*p.y + C*p.y*p.y + 2.*D*p.x + 2.*E*p.y + F ;
    //return d * .0001;

    // via matrix
    mat3 Q = mat3(A, B, D, B, C, E, D, E, F);
    vec3 res = vec3(p, 1) * Q * vec3(p,1);
    d = res.x + res.y + res.z;
    return step(0., d);

}

void mainImage (out vec4 fragColor, in vec2 fragCoord){
    vec3 col = vec3 (0);

    vec2 p = (2. * fragCoord - iResolution.xy)/min(iResolution.x, iResolution.y);

    col = mix(col, vec3(1), qubic(p));

    col = mix(col, vec3(0, 0, 1), lineBox(p));
    col = mix(col, vec3(1, 0, 0), orts(p));



    fragColor = vec4( col, 1);
}
