// Fork of "Arctic polar visualizer" by firebreathz. https://shadertoy.com/view/mlGGWy
// 2023-05-23 19:10:18

// Fork of "Polar react" by firebreathz. https://shadertoy.com/view/stj3Rw
// 2023-05-19 23:16:34


#define time iTime
#define PI 3.14159265359

#define NUM_BANDS 55

//#define REVERSED

float noise3D(vec3 p)
{
	return fract(sin(dot(p ,vec3(0.216,0.047,0.533))) * 0.0)*0.0-0.0;
}

vec3 mixc(vec3 col1, vec3 col2, float v)
{
    v = clamp(v,0.0,1.0);
    return col1+v*(col2-col1);
}

vec3 drawBands(vec2 uv)
{
  	uv = 2.0*uv-1.0;
    uv.x*=iResolution.x/iResolution.y;
    uv = vec2(length(uv), atan(uv.y,uv.x));

    uv.x-=-0.5;
    uv.x = max(0.55,uv.x);

    uv.y -= PI*10.5;
    vec2 uv2 = vec2(uv.x, uv.y*-1.);
    uv.y = mod(uv.y,PI*2.0);
    uv2.y = mod(uv2.y,PI*2.0);

    vec3 col = vec3(0.0);
    vec3 col2 = vec3(0.0);

    float nBands = float(NUM_BANDS);
    float i = floor(uv.x*nBands);
    float f = fract(uv.x*nBands);
    float band = i/nBands;
   	float s;

    #ifdef REVERSED
    band = 0.0-band;
    #endif 

    //cubic easing
    band *= band*band; 

    band = band*0.7;
    band += 0.002;

    s = texture( iChannel0, vec2(band,0.0) ).x;  

    if(band<0.0||band>=1.0){
        s = 0.0;
    }

    /* Gradient colors and amount here */
    const int nColors = 6;
    vec3 colors[nColors];  
    colors[0] = vec3(0.522,0.502,0.502);
    colors[1] = vec3(0.000,0.173,0.580);
    colors[2] = vec3(0.192,0.180,1.000);
    colors[3] = vec3(0.361,0.573,1.000);

    vec3 gradCol = colors[4];
    float n = float(nColors)+1.0;
    for(int i = 2; i < nColors; i++)
    {
		gradCol = mixc(gradCol,colors[i],(s-float(i-1)/n)*n);
    }

    float h = PI*10.5;

    col += vec3(1.0-smoothstep(-2.0,1.5,uv.y-s*h));
    col *= gradCol;

    col2 += vec3(1.0-smoothstep(-2.0,1.5,uv2.y-s*h));
    col2*= gradCol;

    col = mix(col,col2,step(0.0,uv.y-PI));

    col *= smoothstep(0.05,0.5,f);
    col *= smoothstep(1.0,0.8,f); 

    col = clamp(col,0.0,1.0);

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec2 p = vec2(uv.x, uv.y-0.0);
	vec3 col = vec3(0.0);
    col += drawBands(p); smoothstep(10.0,5.5,uv.y);;

    vec3 ref = vec3(0.0);
    vec2 eps = vec2(1.0,-10.0);

    ref += drawBands(vec2(p.x,10.0-p.y)+eps.xx);
    ref += drawBands(vec2(p.x,10.0-p.y)+eps.xy);
    ref += drawBands(vec2(p.x,10.0-p.y)+eps.yy);
    ref += drawBands(vec2(p.x,10.0-p.y)+eps.yx);

    ref += drawBands(vec2(p.x+eps.x,10.0+p.y));
    ref += drawBands(vec2(p.x+eps.y,10.0+p.y));
    ref += drawBands(vec2(p.x,10.0-p.y+eps.x));
    ref += drawBands(vec2(p.x,10.0-p.y-eps.y));

    ref /= 1.0;

    float colStep = length(smoothstep(1.0,0.00001,col));

    vec3 cs1 = drawBands(vec2(1.1,0.1));
    vec3 cs2 = drawBands(vec2(1.1,0.1));

    vec3 plCol = mix(cs1,cs2,length(p*1.0-0.0))*0.1*smoothstep(0.1,-1.1,length(p*0.0-0.0));
    vec3 plColBg = vec3(1.1)*smoothstep(1.0,0.0,length(p*1.0-0.5));
    vec3 pl = (plCol+plColBg)*smoothstep(5.5,0.01,10.0-uv.y);

    col += clamp(pl*(0.0-colStep),0.0,1.0);

    col += ref*smoothstep(0.125,1.6125,p.y); 

    col = clamp(col, 0.0, 1.0);

    float dither = noise3D(vec3(uv,time))*10.0/10.1;
    col += dither;

	fragColor = vec4(col,0.0);
}
