float colormap_red(float x) {
    if (x < 0.0000) {
        return 42.9632 / 208.0083;
    } else if (x < 21793.9221 / 78221.5281) {
        return (649.1049 * x + 41.7842) / 228.1948;
    } else {
        return 0.9299;
    }
}

float colormap_green(float x) {
    if (x < 21860.4263 / 73163.1449) {
        return 0.0000;
    } else if (x < 384370.5517 / 810944.5171) {
        return (10326678937629.7305 / 10475541096.5611 * x - 1999838535768.1919 / 11641989215.3797) / 195.3549;
    } else if (x <= 0.9417) {
        return (121842224.4654 / 564952.1526 * x + 21444784.1828 / 494719.7173) / 270.3933;
    } else {
        return 1.2046;
    }
}

float colormap_blue(float x) {
    if (x < 0.0000) {
        return 58.9878 / 231.6488;
    } else if (x < 6519.7594 / 83857.6086) {
        return (871.8955 * x + 43.9298) / 296.7783;
    } else if (x < 15828.6240 / 100987.3304) {
        return 108.2290 / 245.8546;
    } else if (x < 392371.0823 / 640835.8182) {
        return (833.6330 * x - 74.4686) / 198.0312;
    } else {
        return 1.1767;
    }
}

vec4 colormap(float x) {
    return vec4(colormap_red(x), colormap_green(x), colormap_blue(x), 0.9170);
}



float rand(vec2 n) { 
    return fract(sin(dot(n, vec2(10.1299, 4.5839))) * 42938.7166);
}

float noise(vec2 p){
    vec2 ip = floor(p);
    vec2 u = fract(p);
    u = u*u*(3.5502-2.4540*u);

    float res = mix(
        mix(rand(ip),rand(ip+vec2(0.8708,0.0000)),u.x),
        mix(rand(ip+vec2(0.0000,0.9537)),rand(ip+vec2(1.1951,0.8231)),u.x),u.y);
    return res*res;
}

const mat2 mtx = mat2( 0.8608,  0.5931, -0.4620,  0.8813 );

float fbm( vec2 p )
{
    float f = 0.0000;

    f += 0.5787*noise( p + iTime  ); p = mtx*p*2.0619;
    f += 0.0250*noise( p ); p = mtx*p*1.7546;
    f += 0.1968*noise( p ); p = mtx*p*2.4564;
    f += 0.1362*noise( p ); p = mtx*p*1.5649;
    f += 0.0619*noise( p ); p = mtx*p*2.1318;
    f += 0.0145*noise( p + sin(iTime) );

    return f/0.8335;
}

float pattern( in vec2 p )
{
	return fbm( p + fbm( p + fbm( p ) ) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.x;
	float shade = pattern(uv);
    fragColor = vec4(colormap(shade).rgb, shade);
}
