// Slime Frame
// by leon denise
// 2024/12/23

mat2 rot (float a) { float c=cos(a),s=sin(a); return mat2(c,-s,s,c); }

float smin( float d1, float d2, float k )
{
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h);
}

float map(vec2 p)
{
    vec2 q = p;

    float d = 100.;
    float a = 1.;
    float t = 196.+iTime/20.;

    // break symmetry
    p += vec2(iResolution.x/iResolution.y,1.);
    p *= .6;

    // kaleidoscopic circles
    for (float i = 0.; i < 5.; ++i) {
      p = abs(p)-a;
      float b = length(p);
      p *= rot(t/a+sin(b*5./a)*.05/a);
      p *= 1.+.2*sin(b*4./a+iTime*a+i*3.)*a;
      d = smin(d, abs(b-1.*a), .07);
      a /= 1.4;
    }

    // thickness
    d -= .004;

    // frame
    q = abs(q)-vec2(iResolution.x/iResolution.y, 1.);
    float box = abs(max(q.x, q.y));
    d = smin(d, box-.02, .2);

    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // coordinates
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 p = (2.*fragCoord.xy-iResolution.xy)/iResolution.y;

    // distance
    float d = map(p);

    // normal
    vec2 e = vec2(.002+.02*length(p),0);
    vec3 n = -normalize(vec3(d-vec3(map(p+e.xy), map(p+e.yx), 0.)));

    // lighting
    #define N(x,y,z) normalize(vec3(x,y,z))
    vec3 color = vec3(0);
    color += pow(dot(n, N(0,2,0.5))*.5+.5, 20.);
    color += vec3(0.5,1,0.6) * pow(dot(n, N(0,-.5,-2.))*.5+.5, .7);
    color *= smoothstep(.001,.0,d);

    fragColor = vec4(color,1);
}
