const float pi = 3.14159265358979323846;
vec2
cpx_exp(float exponent)
/* exp() for complex numbers */
{
	vec2 result;

	/* exp(i * x) = cos(x) + i * sin(x) */
	result.x = cos(exponent);
	result.y = sin(exponent);

	return result;
}

/* adjusted to glsl */
float
pm_polynomial_value (int n, int m, float x)
/******************************************************************************/
/*
  Purpose:

    PM_POLYNOMIAL_VALUE evaluates the Legendre polynomials Pm(n,m,x).

  Differential equation:

    (1-X*X) * Y'' - 2 * X * Y + ( N (N+1) - (M*M/(1-X*X)) * Y = 0

  First terms:

    M = 0  ( = Legendre polynomials of first kind P(N,X) )

    Pm(0,0,x) =    1
    Pm(1,0,x) =    1 X
    Pm(2,0,x) = (  3 X^2 -   1)/2
    Pm(3,0,x) = (  5 X^3 -   3 X)/2
    Pm(4,0,x) = ( 35 X^4 -  30 X^2 +   3)/8
    Pm(5,0,x) = ( 63 X^5 -  70 X^3 +  15 X)/8
    Pm(6,0,x) = (231 X^6 - 315 X^4 + 105 X^2 -  5)/16
    Pm(7,0,x) = (429 X^7 - 693 X^5 + 315 X^3 - 35 X)/16

    M = 1

    Pm(0,1,x) =   0
    Pm(1,1,x) =   1 * SQRT(1-X^2)
    Pm(2,1,x) =   3 * SQRT(1-X^2) * X
    Pm(3,1,x) = 1.5 * SQRT(1-X^2) * (5*X^2-1)
    Pm(4,1,x) = 2.5 * SQRT(1-X^2) * (7*X^3-3*X)

    M = 2

    Pm(0,2,x) =   0
    Pm(1,2,x) =   0
    Pm(2,2,x) =   3 * (1-X^2)
    Pm(3,2,x) =  15 * (1-X^2) * X
    Pm(4,2,x) = 7.5 * (1-X^2) * (7*X^2-1)

    M = 3

    Pm(0,3,x) =   0
    Pm(1,3,x) =   0
    Pm(2,3,x) =   0
    Pm(3,3,x) =  15 * (1-X^2)^1.5
    Pm(4,3,x) = 105 * (1-X^2)^1.5 * X

    M = 4

    Pm(0,4,x) =   0
    Pm(1,4,x) =   0
    Pm(2,4,x) =   0
    Pm(3,4,x) =   0
    Pm(4,4,x) = 105 * (1-X^2)^2

  Recursion:

    if N < M:
      Pm(N,M,x) = 0
    if N = M:
      Pm(N,M,x) = (2*M-1)!! * (1-X*X)^(M/2) where N!! means the product of
      all the odd integers less than or equal to N.
    if N = M+1:
      Pm(N,M,x) = X*(2*M+1)*Pm(M,M,x)
    if M+1 < N:
      Pm(N,M,x) = ( X*(2*N-1)*Pm(N-1,M,x) - (N+M-1)*Pm(N-2,M,x) )/(N-M)

  Licensing:

    This code is distributed under the GNU LGPL license.

  Modified:

    08 August 2013

  Author:

    John Burkardt

  Reference:

    Milton Abramowitz, Irene Stegun,
    Handbook of Mathematical Functions,
    National Bureau of Standards, 1964,
    ISBN: 0-486-61272-4,
    LC: QA47.A34.

  Parameters:

    Input, int MM, the number of evaluation points.

    Input, int N, the maximum first index of the Legendre
    function, which must be at least 0.

    Input, int M, the second index of the Legendre function,
    which must be at least 0, and no greater than N.

    Input, double X[MM], the point at which the function is to be
    evaluated.

    Output, double PM_POLYNOMIAL_VALUE[MM*(N+1)], the function values.
*/
{
  float fact;
  int i;
  int j;
  int k;
  float v = 0.0;
  float v0 = 1.0;
  float v1 = x;

/*
  J = M is the first nonzero function.
*/
  if ( m <= n )
  {
    v = 1.0;
    fact = 1.0;
    for ( k = 0; k < m; k++ )
    {
      v = - v * fact * sqrt(1.0 - x * x);
      fact = fact + 2.0;
    }
  }
/*
  J = M + 1 is the second nonzero function.
*/
  if ( m + 1 <= n )
  {
    v = x * (2.0 * float(m) + 1.0) * v;
  }
/*
  Now we use a three term recurrence.
*/
  for ( j = m + 2; j <= n; j++ )
  {
      v = ((2.0 * float(j) - 1.0 ) * x * v1
                  + (- float(j) - float(m) + 1.0 ) * v0 )
                  / float(  j - m     );
      v0 = v1;
      v1 = v;
  }

  return v;
}
/******************************************************************************/
float associated_legendre_function(uint l, int m, float x);
vec2 coordinate_transform(vec2 coordinates);
uint factorial(uint n);
float hydrogen(uint l, int m, uint n, vec3 polar_sph);
float laguerre_polynomials(uint n, uint k, float x);
vec2 spherical_harmonic(uint l, int m, vec2 angles);

float
associated_legendre_function(uint l, int m, float x)
/* https://people.math.sc.edu/Burkardt/c_src/legendre_polynomial/legendre_polynomial.html */
{
	return pm_polynomial_value(int(l), m, x);
}

uint
factorial(uint n)
/* 0 <= n <= 12 */
{
/* OEIS: A000142 */
    uint LUT[13] = uint[13](1U,
    1U,
    2U,
    6U,
    24U,
    120U,
    720U,
    5040U,
    40320U,
    362880U,
    3628800U,
    39916800U,
    479001600U
    );

	if (n > 12U) {
		return uint(0);
	} else  {
		return LUT[n];
	}
}

float
laguerre_polynomials(uint n, uint k, float x)
/* solve a given laguerre polynomial numerically */
/* tested for correctness on n=2,k=3 using (x*x - 2*(k+2)*x + (k+1)*(k+2))/2 */
{
	float lp0 = 1.0;
	float lp1 = 1.0 + float(k) - x;

	if (n < 2U) {
		if (n == 1U) return lp1;
		if (n == 0U) return lp0;
	}

	uint degree;
	float result;
	/* using the recursive definition */
	for (degree = 2U; degree <= n; degree++) {
		result =
			(
				(float(2U * (degree-1U) + 1U + k) - x) * lp1 - float(degree - 1U + k) * lp0
			) / float(degree);
		lp0 = lp1;
		lp1 = result;
	}

	return result;
}

vec2
spherical_harmonic(uint l, int m, vec2 angles)
/* using eq. (6) for Y from https://mathworld.wolfram.com/SphericalHarmonic.html */
{
	/* different naming convention: psi, theta switch */
	float psi = angles.x; /* azimuthal */
	float theta = angles.y; /* polar */

	/* p_lm is an associated Legendre polynomial */
	float p_lm = associated_legendre_function(l, m, cos(theta));
	float scalar = p_lm * sqrt(
		(float(2U * l + 1U))
		/ (float(4) * pi)
		* float(factorial(uint(int(l) - m)))
		/ float(factorial(uint(int(l) + m))));
	/* a complex number is a multivector */
	vec2 multivector = cpx_exp(float(m) * psi) * scalar;

	return multivector;
}

float
hydrogen(uint l, int m, uint n, vec3 polar_sph)
/* solve the Schrödinger equation for a hydrogen atom */
/* $ \psi _{n\ell m}(r,\theta ,\varphi )={\sqrt {\left({\frac {2}{na_{0}}}\right)^{3}{\frac {(n-\ell -1)!}{2n[(n+\ell )!]}}}}e^{-r/na_{0}}\left({\frac {2r}{na_{0}}}\right)^{\ell }L_{n-\ell -1}^{2\ell +1}\left({\frac {2r}{na_{0}}}\right)\cdot Y_{\ell }^{m}(\theta ,\varphi ) $ */
{
	float a_0 /* bohr radius */ = 1.0; /* =1 for the sake of reducing floating point error */
	/*
	quantum numbers
	l azimuthal
	m magnetic
	n principal
	*/

	float na_0 = float(n) * a_0;
	float r = polar_sph.x;

	vec2 result;
	float scalar;
	{/* {\sqrt {\left({\frac {2}{na_{0}}}\right)^{3}{\frac {(n-\ell -1)!}{2n[(n+\ell )!]}}}} */
		float exp3 = pow(2.0 / na_0, 3.0);
		float fact_n = float(factorial(n - l - 1U));
		float fact_d = float(2U * n * factorial(n + l));
		float fract = fact_n / fact_d;
		scalar = sqrt(exp3 * fract);
	}
	scalar *= exp(-r / na_0);
	scalar *= pow(2.0*r / na_0, float(l));
	scalar *= laguerre_polynomials(n - l - 1U, 2U * l + 1U, 2.0 * r / na_0);

	result = scalar * spherical_harmonic(l, m, polar_sph.yz);

	return length(result);
}

vec2
coordinate_transform(vec2 coordinates)
/* map to [-1, 1], (0, 0) is the center */
{
	/* dividing by just y to have a square screen */
	vec2 transformed = (coordinates / iResolution.y);
	/* translate center to screen center */
	/* aspect/2 -> 1/2 */
	float aspect_ratio = iResolution.x / iResolution.y;
	transformed.x -= aspect_ratio / 2.0 - 0.5;
	/* [0,1] -> [-1,1] */
	transformed = transformed * 2.0 - 1.0;

	return transformed;
}

vec3
srgb2linear(vec3 srgb)
/* https://www.color.org/chardata/rgb/srgb.xalter */
{
	vec3 linear;

	linear.x = pow(((srgb.x + 0.055) / 1.055), 2.4) / 12.92;
	linear.y = pow(((srgb.y + 0.055) / 1.055), 2.4) / 12.92;
	linear.z = pow(((srgb.z + 0.055) / 1.055), 2.4) / 12.92;

	return linear;
}

void
mainImage(out vec4 c, vec2 u)
{
	vec3 xyz = vec3(0, coordinate_transform(u).yx);/* slice in yz plane */

	float theta = atan(xyz.x, xyz.y);/* azimuthal angle */
	float psi = atan(xyz.z, length(xyz.xy)); /* polar angle */

	/* switch between some orbitals */
	/* l, m, n */
	const ivec3 orbitals[] = ivec3[](
	/* 1^2 */
	ivec3(0, 0, 1),
	/* 2^2 */
	ivec3(0, 0, 2),
	ivec3(1, 0, 2),
	ivec3(1, +1, 2),
	ivec3(1, -1, 2),
	/* 3^2 */
	ivec3(0, 0, 3),
	ivec3(1, 0, 3),
	ivec3(1, +1, 3),
	ivec3(1, -1, 3),
	ivec3(2, 0, 3),
	ivec3(2, +1, 3),
	ivec3(2, -1, 3),
	ivec3(2, +2, 3),
	ivec3(2, -2, 3),
	/* 4^2 */
	ivec3(0, 0, 4),
	ivec3(1, 0, 4),
	ivec3(1, +1, 4),
	ivec3(1, -1, 4),
	ivec3(2, 0, 4),
	ivec3(2, +1, 4),
	ivec3(2, -1, 4),
	ivec3(2, +2, 4),
	ivec3(2, -2, 4),
	ivec3(3, 0, 4),
	ivec3(3, +1, 4),
	ivec3(3, -1, 4),
	ivec3(3, +2, 4),
	ivec3(3, -2, 4),
	ivec3(3, +3, 4),
	ivec3(3, -3, 4)
	);




	/* transition every 500 ms */
	uint index = (uint(iTime * 2.0) % uint(orbitals.length()));
	int l, m, n;
	l = orbitals[index].x;
	m = orbitals[index].y;
	n = orbitals[index].z;

	float zoom = float(15U + index * 2U);
	/* spherical coordinates */
	vec3 position = vec3(length(xyz) * zoom, theta, psi);

	c.x = hydrogen(uint(l), abs(m), uint(n), position);

	// color-primary #FF5053 of https://events.ccc.de/congress/2024/infos/styleguide.html
	vec3 reference_primary = srgb2linear(vec3(255, 80, 83));
	vec3 palette = vec3(reference_primary / 1e3);
	c.xyz = c.xxx * palette;
    c.w = 1.0;
}
