const vec2  g_sphere_center     = vec2(0.0, 0.0);
const float g_sphere_radius     = 0.5;
const float g_subsurface_radius = 0.45;

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    fragColor = vec4(0.0, 0.0, 0.0, 1.0);

    vec2 ndc = (fragCoord / iResolution.xy) * 2.0 - 1.0;
    ndc.x *= iResolution.x / iResolution.y;

    if (abs(ndc.x) >= 1.0 || abs(ndc.y) >= 1.0) {
        fragColor = vec4(0.4, 0.4, 0.4, 1.0);
        return;
    }

    float dist = distance(g_sphere_center, ndc);

    float sphere_norm_dist     = dist / g_sphere_radius;
    float subsurface_norm_dist = dist / g_subsurface_radius;

    fragColor.x = sqrt(acos(sphere_norm_dist)) - sqrt(acos(subsurface_norm_dist));
    fragColor.y = fragColor.x;
    fragColor.z = fragColor.x;
}
