void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.x;
    uv.x -= 0.5;
    uv.y -= 0.3;
    vec2 Focus1 = vec2(0.1, 0.0);
    vec2 Focus2 = vec2(-0.1, 0.0);
    float c = abs(sin(iTime/3.0)/5.0);
    float length1 = abs(length(uv-Focus1));
    float length2 = abs(length(uv-Focus2));
    fragColor = vec4(0.003/(length1) + 0.003/(length2), 0.11/(abs((length1 + length2) - c*1.5)*10.0), 0.01/abs((abs(length1 - length2) - c)*4.0), 0.0);
}
