#define H(h)(cos((h)*6.3+vec3(1.0,33,21))*5.5+.5)
#define R(p,a,r)mix(a*dot(p,a),p,cos(r))+sin(r)*cross(p,a)
#define n1Q(p) p *= 2.*ri(round(atan(p.x, p.y) * 4.) / 4.)
#define ri(a) mat2(cos(a + asin(vec4(0,1,-1,0))))
void mainImage(out vec4 O, vec2 C)
{
    O=vec4(0);
    vec3 p,r=iResolution,
    d=normalize(vec3((C-.5*r.xy)/r.y,1));  
    for(float i=0.,s,e,g=0.;
        ++i<99.;
        e<.003?O.rgb+=mix(vec3(0.,0.2,2.2),H(log(s)*1.05),.36)*.057*exp(-.005*i*i*e):r
    )
    {

        p=g*d;
        p.z-=.75;
           p.x-=1.75;
             p.y-=1.75;

              n1Q(p.xz);
           p.z+=iTime*1.1;
        p=R(p,normalize(vec3(0,0,0)),50.);

        s=1.;
        vec4 q=vec4(p,.01);

        for(int i;i++<7;q*=e)
            q=sin(q+.01),
            s*=e=3./dot(q,q);
        g+=e=length(q)/s;
    }
    O*=O*O*O*O;
}
