vec3 circ(vec2 pos) 
{
    float dist = distance(pos - vec2(0.5, 0.5), vec2(cos(iTime), sinh(sin(iTime))));
    return vec3(0.5 + 0.5 * cos(iTime), 0.5 + 0.5 * sin(iTime), 0.5) * clamp(1.0 - dist, 0.0, 0.75 + 0.25 * cos(iTime * 0.1));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = (circ(uv.xy) + circ(uv.yx) + circ(vec2(1,1) - uv.xy) + circ(vec2(1,1) - uv.yx)) * 0.25;

    // Output to screen
    fragColor = vec4(col,1.0);
}
