#define I iResolution
#define A min( I.x, I.y )
#define h(u) fract(dot( floor(u.yxx * 123.45), sin(u.xyy) / 123.456 ) )
#define rot( a ) mat2( cos(a), -sin( a ), sin(a), cos(a) )

float tex( vec2 u ){
    float p, q;

    u *= rot( iTime );

    u = abs( u );
    u += sign( u.x + u.y ) * -.6;

    u *= rot( iTime );

    p = fract( length( u ) * 8. - .2 );

    p = max(
        p, 
        length( u ) - .5
    );

    p = smoothstep( 50. / A, 0., p );

    return p * step( fract( atan( u.x, u.y ) / 6.28 - h( (fract( u-.5 ) + .5) ) ), .9 );
}

void mainImage( out vec4 U, in vec2 V )
{


    float p, i = 6.;

    while( i-- > 1. ){
        p += tex( (( V * 2. - I.xy ) / A) * i + vec2( cos(iTime - i), sin(iTime - i) ) ) * ( 6. - i );
    }


    U = vec4( p );
}
