void mainImage( out vec4 O, in vec2 F )
{
    O = texture(iChannel0,F/iResolution.xy);
}

void mainImage( out vec4 O, in vec2 F )
{
    F/=iResolution.xy;
    O=vec4(0);
    if (iFrame == 1)
    {
        if (1.-(F.y+abs(F+F-1.).x)>0.) O = vec4(1);
    }
    else
    {
        if (F.y < .5) O = texture(iChannel0,fract(F+F));
        else
        {
            if (F.x>.25 && F.x<.75) O = texture(iChannel0,F+F-vec2(.5,0));
        }
    }
}
