void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec3 col = vec3(0);
    float fork = smoothstep(0., .5, (uv.y)-0.01);

    float separation = fork * .2;
    // abs(uv.x) makes a mirror over y axis
    // separation is de distance between the mirrored lines
    float bifurcationWidth = (1. - fork) * 0.8 + 0.2;
    float m = smoothstep(0.05 * bifurcationWidth , 0., abs(abs(uv.x)-separation));
    col += m;

    // Output to screen
    fragColor = vec4(col,1.0);
}
