void mainImage(out vec4 fragColor, in vec2 fragCoord) {

  float t = 0.05 * iTime;
  fragCoord *= mat2(sin(t), cos(t), -cos(t), sin(t));
  vec3 col = vec3(1.0);

  float real_freq = 1. / 1.1;
  float x = fragCoord.x;
  float y = fragCoord.y;

  col *= 1. - step(.5, abs(mod(x, real_freq + 1.)));
  col *= 1. - step(.5, abs(mod(y, real_freq + 1.)));
  fragColor = vec4(col, 1.0);
}
