void mainImage(out vec4 fragColor, in vec2 fragCoord) {

    vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;


    float t = iTime * 0.5;  
    float wave1 = sin(uv.x * 5.0 + t) + cos(uv.y * 10.0 + t);
    float wave2 = sin(uv.y * 3.0 - t) * cos(uv.x * 6.0 + t);


    float colorIntensity = wave1 + wave2;


    vec3 color = vec3(0.5 + 0.5 * cos(colorIntensity * 2.0 + t),
                      0.5 + 0.5 * sin(colorIntensity * 3.0 - t),
                      0.5 + 0.5 * tan(colorIntensity * 4.0 + t));


    fragColor = vec4(color, 1.0);
}
